import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/styles";
import {
  Button,
  TextField,
  FormControl,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormLabel,
} from "@material-ui/core";
import { Link } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: 3,
  },
}));

export function AmiiboCharacter({ match }) {

  console.log(match);

  const classes = useStyles();
  const [character, setCharacter] = useState()

  useEffect(() => {
    fetch(`https://www.amiiboapi.com/api/amiibo?id=${match.params.id}`).then(
      (response) =>
        response.json().then(data => {
            setCharacter(data.amiibo)
            console.log(data);
        })
    );
  }, []);

  return(
      <div style={{width: '50%', marginLeft: 'auto', marginRight: 'auto'}}>
          <h2>Amiibo Edition</h2>

          <p>
              <TextField id='name' value={character.name} defaultValue='none' fullWidth='true' label='Name' variant='outlined'/>
              <TextField id='serie' value={character.amiiboSeries} defaultValue='none' fullWidth='true' label='Serie' variant='outlined'/>
              <TextField id='character' value={character.character} defaultValue='none' fullWidth='true' label='Character' variant='outlined'/>
              <TextField id='gameSeries' value={character.gameSeries} defaultValue='none' fullWidth='true' label='Game Series' variant='outlined'/>

              <FormControl component='fieldset' className={classes.formControl}>
                  <FormLabel component='legend'>Type:</FormLabel>
                  <RadioGroup aria-label='position' name='position' value={character.type} row>
                      <FormControlLabel value='Figure' control={<Radio color='primary' />} label='Figure' labelPlacement='top' />

                      <FormControlLabel value='Yarn' control={<Radio color='primary' />} label='Yarn' labelPlacement='top' />
                  </RadioGroup>
              </FormControl>
          </p>
      </div>
  )
}
